import React, {useState} from "react";
import './style.css';
import data from "./TemplateData.json";




function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedYear, setSelectedYear] = useState("");

  return (
    <>
      <div className="templateContainer">
        <div className="filterContainer">
          <div className="searchInput_Container">
            <input
              id="searchInput"
              type="text"
              placeholder="Search by title or year..."
              value={searchTerm}
              onChange={(event) => setSearchTerm(event.target.value)}
            />
          </div>
          <div className="yearFilterContainer">
            <label htmlFor="yearFilter">Filter by Year:</label>
            <select
              id="yearFilter"
              onChange={(event) => {
                console.log("Selected Year:", event.target.value);
                setSelectedYear(event.target.value);
              }}
            >
              <option value="">All Years</option>
              {Array.from(new Set(data.map((item) => item.year))).map((year) => (
                <option key={year} value={year.toString()}>{year}</option>
              ))}
            </select>
          </div>
        </div>
        <div className="template_Container">
          {
            data
              .filter((val) => {
                const titleMatch = val.title.toLowerCase().includes(searchTerm.toLowerCase());
                const yearMatch = selectedYear ? val.year.toString() === selectedYear : true;

                console.log("Title:", val.title, "Year:", val.year, "Year Match:", yearMatch);

                return titleMatch && yearMatch;
              })
              .map((val) => (
                <div className="template" key={val.id}>
                  <a href={val.link} target="_blank" rel="noopener noreferrer">
                    <img src={val.image} alt="" />
                    <h3>{val.title}</h3>
                    {val.year && <p>Year: {val.year}</p>}
                  </a>
                </div>
              ))
          }
        </div>
      </div>
    </>
  );
}



export default App;
