import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

// Assuming Data is your array of objects
const data = [
  // Your data here
];

function TemplateDetail({ match }) {
  const templateId = match.params.id;
  const template = data.find((item) => item.id === parseInt(templateId));

  if (!template) {
    return <div>Template not found</div>;
  }

  return (
    <div>
      <h2>{template.title}</h2>
      <p>Year: {template.year}</p>
      <img src={template.image} alt={template.title} />
      <p>Description: {template.description}</p>
      <a href={template.link} target="_blank" rel="noopener noreferrer">
        View Details
      </a>
    </div>
  );
}

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortByYear, setSortByYear] = useState(false);

  const sortedData = [...data];

  if (sortByYear) {
    sortedData.sort((a, b) => (a.year > b.year ? 1 : -1));
  }

  return (
    <Router>
      <>
        <div className="templateContainer">
          <div className="filterContainer">
            <div className="searchInput_Container">
              <input
                id="searchInput"
                type="text"
                placeholder="Search by title or year..."
                value={searchTerm}
                onChange={(event) => setSearchTerm(event.target.value)}
              />
            </div>
            <div className="yearFilterContainer">
              <label htmlFor="sortFilter">Sort by Year:</label>
              <input
                id="sortFilter"
                type="checkbox"
                checked={sortByYear}
                onChange={() => setSortByYear(!sortByYear)}
              />
            </div>
          </div>
          <div className="template_Container">
            {sortedData
              .filter((val) => {
                const titleMatch = val.title.toLowerCase().includes(searchTerm.toLowerCase());
                const yearMatch = val.year ? val.year.toString().includes(searchTerm) : false;
                return titleMatch || yearMatch;
              })
              .map((val) => (
                <div className="template" key={val.id}>
                  <Link to={`/template/${val.id}`}>
                    <img src={val.image} alt="" />
                    <h3>{val.title}</h3>
                    {val.year && <p>Year: {val.year}</p>}
                  </Link>
                </div>
              ))}
          </div>
        </div>

        <Route path="/template/:id" component={TemplateDetail} />
      </>
    </Router>
  );
}

export default App;
