import React, {useState} from "react";
import './style.css';
import data from "./TemplateData.json";


import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
//import './style.css';
///import data from './TemplateData.json';
import Home from './Home'; // Create a Home component
import Details from './Details'; // Create a Details component

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortByYear, setSortByYear] = useState(false);

  const sortedData = [...data];

  if (sortByYear) {
    sortedData.sort((a, b) => (a.year > b.year ? 1 : -1));
  }

  return (
    <Router>
      <div className="templateContainer">
        <div className="filterContainer">
          <div className="searchInput_Container">
            <input
              id="searchInput"
              type="text"
              placeholder="Search by title or year..."
              value={searchTerm}
              onChange={(event) => setSearchTerm(event.target.value)}
            />
          </div>
          <div className="yearFilterContainer">
            <label htmlFor="sortFilter">Sort by Year:</label>
            <input
              id="sortFilter"
              type="checkbox"
              checked={sortByYear}
              onChange={() => setSortByYear(!sortByYear)}
            />
          </div>
        </div>
        <Route path="/details" component={Details} />
        <Route
          exact
          path="/"
          render={() => <Home data={sortedData} searchTerm={searchTerm} />}
        />
      </div>
    </Router>
  );
}
<Route
  path="/"
  render={() => <Home data={sortedData} searchTerm={searchTerm} />}
/>
export default App;
